$(document).ready(function(){$("#mailform").validate({errorClass:"error-text",rules:{name:{required:!0},subject:{required:!0},message:{required:!0},name:{required:!0},address:{required:!0,email:!0}},messages:{name:"Enter your name",subject:"Enter a subject",message:"Enter a message",address:{required:"Please enter a valid email address",email:"Please enter a valid email address"}},errorPlacement:function(a,b){b.closest(".controls");b.closest(".controls").find(".error").remove();$('<div class="error_wrapper">').append($('<div class="error">').html(a.text())).insertAfter(b)}, submitHandler:function(a){a.submit()}})});